import React, { useState, useEffect } from "react";
import "./Navbar.css";
import CreateModal from "./Modals/CreateModal";

const Navbar = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [userName, setUserName] = useState('');
  const [showLogout, setShowLogout] = useState(false); // State for showing/hiding logout button

  // Fetch username from localStorage when the component mounts
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    if (storedName) {
      setUserName(storedName);
    }
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "ðŸŒ„ Good morning";
    else if (hour < 18) return "â˜€ï¸ Good afternoon";
    else return "ðŸŒ™ Good evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };

  const capitalizeFirstLetter = (userName) => {
    if (!userName) return "Guest"; // Default if userName is not provided
    return userName
      .split(" ") // Split the name into words
      .map(word =>
        word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() // Capitalize the first letter of each word
      )
      .join(" "); // Join the words back together with a space
  };

  // Handle the profile icon click to show/hide the logout button
  const handleProfileClick = () => {
    setShowLogout(prevState => !prevState); // Toggle the logout button visibility
  };

  // Handle logout action
  const handleLogout = () => {
    // Clear user data from localStorage and reset state
    localStorage.removeItem('userName');
    setUserName('');
    setShowLogout(false); // Hide the logout button
  };

  return (
    <>
      <nav className="navbar">
        <div className="navbar-left">
          <span className="greeting">
            {`${getGreeting()}, ${capitalizeFirstLetter(userName) || 'Guest'}`}
          </span>
          <div className="day">
            <span className="date">{getFormattedDate()}</span>
          </div>
        </div>
        <div className="navbar-right">
          <button
            className="create-button"
            onClick={() => setModalOpen(true)}
          >
            + Create new
          </button>
          <input
            type="text"
            placeholder="Search by remarks"
            className="search-input"
          />
          <div className="profile-icon" onClick={handleProfileClick}>
            {
              userName
                ? userName.split(' ').length > 1
                  ? userName.split(' ')[0].charAt(0).toUpperCase() + userName.split(' ')[1].charAt(0).toUpperCase()
                  : userName.charAt(0).toUpperCase() + userName.charAt(userName.length - 1).toUpperCase()
                : 'G'
            }
          </div>
          {showLogout && (
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          )}
        </div>
      </nav>

      {/* Modal Component */}
      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
    </>
  );
};

export default Navbar;
