import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CreateModal from "./Modals/CreateModal";
import Dashpart from "./Modals/Dashpart";
import Data from "./Modals/Data";
import Setting from "./Modals/Setting";
import "./Dashboard.css";
import "./Menu.css";
import "./Navbar.css";
import "./Phone.css";

const Dashboard = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false);
  const [userName, setUserName] = useState("");
  const [showLogout, setShowLogout] = useState(false);
  const [isPhoneMode, setIsPhoneMode] = useState(false);
  const [activeComponent, setActiveComponent] = useState("");
  const [searchTerm, setSearchTerm] = useState(""); // State for search input

  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => {
      setIsPhoneMode(window.innerWidth <= 1080);
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    const storedName = localStorage.getItem("userName");

    if (!token) {
      navigate("/login");
    }

    if (storedName) {
      setUserName(storedName);
    }
  }, [navigate]);

  const getGreeting = () => {
    const hour = new Date().getHours();
    return hour < 12
      ? "ðŸŒ„ Good morning"
      : hour < 18
      ? "â˜€ï¸ Good afternoon"
      : "ðŸŒ™ Good evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };

  const capitalizeFirstLetter = (userName) => {
    if (!userName) return "Guest";
    return userName
      .split(" ")
      .map(
        (word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
      )
      .join(" ");
  };

  const handleProfileClick = () => {
    setShowLogout((prevState) => !prevState);
  };

  const handleLogout = () => {
    localStorage.removeItem("userName");
    setUserName("");
    setShowLogout(false);
    navigate("/");
  };

  const handleDashboardClick = () => setActiveComponent("dashpart");
  const handleDataClick = () => setActiveComponent("data");
  const handleSettingsClick = () => setActiveComponent("settings");

  return (
    <div className="dashboard-container">
      <div className="rightpart">
        <nav className="navbar">
          <div className="navbar-left">
            <span className="greeting">
              {`${getGreeting()}, ${capitalizeFirstLetter(userName) || "Guest"}`}
            </span>
            <div className="day">
              <span className="date">{getFormattedDate()}</span>
            </div>
          </div>
          <div className="navbar-right">
            <button
              className="create-button"
              onClick={() => setModalOpen(true)}
            >
              + Create new
            </button>
            <input
              type="text"
              placeholder="Search by remarks"
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)} // Update searchTerm
            />
            {showLogout && (
              <button className="logout-button" onClick={handleLogout}>
                Logout
              </button>
            )}
            <div className="profile-icon" onClick={handleProfileClick}>
              {userName
                ? userName.split(" ").length > 1
                  ? userName.split(" ")[0].charAt(0).toUpperCase() +
                    userName.split(" ")[1].charAt(0).toUpperCase()
                  : userName.charAt(0).toUpperCase() +
                    userName.charAt(userName.length - 1).toUpperCase()
                : "G"}
            </div>
          </div>
        </nav>
      </div>

      <div className="content-dash">
        <div className={`menu-container ${isPhoneMode ? "hidden" : ""}`}>
          <div className={`menu-content ${isMenuOpen ? "open" : "closed"}`}>
            <ul className="menu-items">
              <li className="menu-item" onClick={handleDashboardClick}>
                Dashboard
              </li>
              <li className="menu-item" onClick={handleDataClick}>
                Links
              </li>
              <li className="menu-item" onClick={handleSettingsClick}>
                Settings
              </li>
            </ul>
          </div>
        </div>

        <div className="mid-content">
          {activeComponent === "dashpart" && <Dashpart />}
          {activeComponent === "data" && <Data searchTerm={searchTerm} />}
          {activeComponent === "settings" && <Setting />}
        </div>
      </div>

      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
    </div>
  );
};

export default Dashboard;
