import React, { useState, useEffect } from "react";
import "./Navbar.css";
import CreateModal from "./Modals/CreateModal";

const Navbar = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [userName, setUserName] = useState('');

  // Fetch username from localStorage when the component mounts
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    console.log('Fetched userName from localStorage:', storedName); // Check if the value is correct
    if (storedName) {
      setUserName(storedName);
    } else {
      console.log("User name not found in localStorage");
    }
  }, []);
  
  

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "ðŸŒ„ Good morning";
    else if (hour < 18) return "â˜€ï¸ Good afternoon";
    else return "ðŸŒ™ Good evening";
  };

  const getFormattedDate = () => {
    const today = new Date();
    const options = { weekday: "short", month: "short", day: "numeric" };
    return today.toLocaleDateString(undefined, options);
  };
  const getUserInitials = (userName) => {
    if (!userName) return "G"; // Default if userName is not provided
    
    const words = userName.split(" "); // Split the name by space
    if (words.length === 1) {
      // If there's only one word, show the first and last letter in uppercase
      return `${words[0].charAt(0).toUpperCase()}${words[0].charAt(words[0].length - 1).toUpperCase()}`;
    } else {
      // If there are two words, show the first letter of each word in uppercase
      return `${words[0].charAt(0).toUpperCase()}${words[1].charAt(0).toUpperCase()}`;
    }
  };
  
  // Usage in JSX:
  <div className="profile-icon">
    {getUserInitials(userName)}
  </div>
  

  return (
    <>
      <nav className="navbar">
        <div className="navbar-left">
          <span className="greeting">
            {`${getGreeting()}, ${ getUserInitials(userName) || 'Guest'}`}
          </span>
          <div className="day">
            <span className="date">{getFormattedDate()}</span>
          </div>
        </div>
        <div className="navbar-right">
          <button
            className="create-button"
            onClick={() => setModalOpen(true)}
          >
            + Create new
          </button>
          <input
            type="text"
            placeholder="Search by remarks"
            className="search-input"
          />
          <div className="profile-icon">
            {/* Display user's first letter if username is available, otherwise 'G' for guest */}
            {
  userName
    ? userName.split(' ').length > 1
      ? userName.split(' ')[0].charAt(0).toUpperCase() + userName.split(' ')[1].charAt(0).toUpperCase()
      : userName.charAt(0).toUpperCase() + userName.charAt(userName.length - 1).toUpperCase()
    : 'G'
}



          </div>
        </div>
      </nav>

      {/* Modal Component */}
      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
    </>
  );
};

export default Navbar;
