import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CreateModal from "./Modals/CreateModal";
import Dashpart from "./Modals/Dashpart";
import Data from "./Modals/Data";
import Setting from "./Modals/Setting";
import "./Dashboard.css";
import "./Menu.css";
import "./Navbar.css";
import "./Phone.css";

const Dashboard = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false);
  const [userName, setUserName] = useState("");
  const [showLogout, setShowLogout] = useState(false);
  const [isPhoneMode, setIsPhoneMode] = useState(false);
  const [activeSection, setActiveSection] = useState("dashboard"); // Active section state
  const navigate = useNavigate();

  // Update phone mode on resize
  useEffect(() => {
    const handleResize = () => setIsPhoneMode(window.innerWidth <= 1080);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Redirect if user is not logged in
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    const storedName = localStorage.getItem("userName");

    if (!token) navigate("/login");
    if (storedName) setUserName(storedName);
  }, [navigate]);

  // Get greeting based on the time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    return hour < 12 ? "üåÑ Good morning" : hour < 18 ? "‚òÄÔ∏è Good afternoon" : "üåô Good evening";
  };

  // Format date
  const getFormattedDate = () => {
    const options = { weekday: "short", month: "short", day: "numeric" };
    return new Date().toLocaleDateString(undefined, options);
  };

  // Capitalize user name
  const capitalizeName = (name) =>
    name
      ? name
          .split(" ")
          .map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
          .join(" ")
      : "Guest";

  // Handle profile click (toggle logout button visibility)
  const handleProfileClick = () => setShowLogout((prev) => !prev);

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("userName");
    setUserName("");
    navigate("/");
  };

  // Render content based on active section
  const renderContent = () => {
    switch (activeSection) {
      case "dashboard":
        return <Dashpart />;
      case "data":
        return <Data />;
      case "settings":
        return <Setting />;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-left">
          <span className="greeting">
            {`${getGreeting()}, ${capitalizeName(userName)}`}
          </span>
          <span className="date">{getFormattedDate()}</span>
        </div>
        <div className="navbar-right">
          <button className="create-button" onClick={() => setModalOpen(true)}>
            + Create new
          </button>
          <input type="text" placeholder="Search by remarks" className="search-input" />
          {showLogout && (
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          )}
          <div className="profile-icon" onClick={handleProfileClick}>
            {userName
              ? userName.charAt(0).toUpperCase() + userName.charAt(userName.length - 1).toUpperCase()
              : "G"}
          </div>
        </div>
      </nav>

      {/* Sidebar Menu */}
      <div className={`menu-container ${isPhoneMode ? "hidden" : ""}`}>
        <ul className="menu-items">
          <li
            className={`menu-item ${activeSection === "dashboard" ? "active" : ""}`}
            onClick={() => setActiveSection("dashboard")}
          >
            <span className="menu-icon">üìä</span>
            <span className="menu-text">Dashboard</span>
          </li>
          <li
            className={`menu-item ${activeSection === "data" ? "active" : ""}`}
            onClick={() => setActiveSection("data")}
          >
            <span className="menu-icon">üîó</span>
            <span className="menu-text">Links</span>
          </li>
          <li
            className={`menu-item ${activeSection === "settings" ? "active" : ""}`}
            onClick={() => setActiveSection("settings")}
          >
            <span className="menu-icon">‚öôÔ∏è</span>
            <span className="menu-text">Settings</span>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="content-dash">{renderContent()}</div>

      {/* Create Modal */}
      <CreateModal isOpen={isModalOpen} onClose={() => setModalOpen(false)} />
    </div>
  );
};

export default Dashboard;
